import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Title from "./components/Title";
import Menu from "./components/Menu";
import "./index.css";

const sectionsObject = {
  home: "Home",
  poultry: "Poultry",
  pork: "Pork",
  fish: "fish",
  vegetarian: "Vegetarian"
};
const sections = ["Home", "Poultry", "Pork", "Fish", "Vegetarian", "Salads"];

export default function BasicExample() {
  return (
    <div className="main-page">
      {/*
          A <Switch> looks through all its children <Route>
          elements and renders the first one whose path
          matches the current URL. Use a <Switch> any time
          you have multiple routes, but you want only one
          of them to render at a time
        */}
      <MainPage />
    </div>
  );
}

const MainPage = () => {
  const [menuIsOpen, setMenuIsOpen] = useState(false);
  const [menuSection, setMenuSection] = useState(sections[0]);

  const changeMenuStatus = () => {
    setMenuIsOpen(!menuIsOpen);
  };

  return (
    <React.Fragment>
      <Title titleText="EconomicEats" openCloseMenu={changeMenuStatus} />
      {menuIsOpen && <Menu array={sections} sectionChange={setMenuSection} />}
      <div className="content">
        Eatin healthy can be very difficult, and even more difficult when you're
        on a budget. As a student who tries to eat healthy, I know just how
        expensive it can be. The goal of this website is to provide people
        eating on a budget healthy, delicious, and inexpensive meals. This
        website includes recipes featuring Poultry, Pork, and Fish. As well as
        Vegetarian meals and delicious Salads. The Recipes can be found through
        the menu off to the side or by clicking the options below
      </div>
    </React.Fragment>
  );
};
