import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./index.css";
import HelloWorldPage from "./pages/HelloWorldPage";
import CalculatorPage from "./pages/CalculatorPage";
import IconBox from "./components/IconBox";
import Title from "./components/Title";
import helloWorldImage from "./images/helloWorld.PNG";
import calculatorImage from "./images/Calculator.png";

export default function BasicExample() {
  const [showMainDescript, setShowMainDescript] = React.useState(true);
  return (
    <Router>
      <div className="main-page">
        <div className="centered-horizontal-flex">
          <IconBox route="/hello" imageSrc={helloWorldImage} />
          <IconBox route="/calculator" imageSrc={calculatorImage} />
        </div>
        {showMainDescript && <React.Fragment></React.Fragment>}

        {/*
          A <Switch> looks through all its children <Route>
          elements and renders the first one whose path
          matches the current URL. Use a <Switch> any time
          you have multiple routes, but you want only one
          of them to render at a time
        */}
        <Switch>
          <Route exact path="/hello">
            <HelloWorldPage />
          </Route>
          <Route exact path="/calculator">
            <CalculatorPage />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

const mainPage = () => {
  return (
    <React.Fragment>
      <Title titleText="React Practice Site" />
      <div className="content">
        <div className="page-description">
          Welcome to Michael's React Practice Site, This site will be full of
          mini react projects for me to Practice my React and web development
          skills. Click The icons at the top of the page to switch between
          different projects.
        </div>
      </div>
    </React.Fragment>
  );
};
