import React, { useState } from "react";
import "./Menu.css";

const Menu = ({ array, sectionChange }) => {
  return (
    <div className="menu">
      <div className="line">CATEGORIES</div>
      {array.map(sectionName => (
        <div className="section" onClick={() => sectionChange(sectionName)}>
          {sectionName}
        </div>
      ))}
    </div>
  );
};

export default Menu;
